/**
 * Applies styles for users in high contrast mode. Note that this only applies
 * to Microsoft browsers. Chrome can be included by checking for the `html[hc]`
 * attribute, however Chrome handles high contrast differently.
 */
/* Theme for the ripple elements.*/
/* stylelint-disable material/no-prefixes */
/* stylelint-enable */
:host {
  display: grid;
  width: 100%;
  height: 100vh;
  grid-gap: 0;
  grid-template-columns: 1fr;
  grid-template-rows: 115px 1fr 50px;
  grid-template-areas: "main-header" "router-outlet" "main-footer"; }
  :host > header {
    grid-area: main-header;
    background: white;
    box-shadow: 0px 0px 15px -1px rgba(117, 117, 117, 0.4); }
  :host > section {
    grid-area: router-outlet;
    max-height: calc(100vh - (115px + 50px));
    padding: 20px 10px; }
    :host > section perfect-scrollbar {
      max-height: calc(100vh - (115px + 50px + 40px));
      background: white;
      box-shadow: 0px 0px 15px -1px rgba(117, 117, 117, 0.4); }
  :host > footer {
    grid-area: main-footer;
    background: white;
    box-shadow: 0px 0px 15px -1px rgba(117, 117, 117, 0.4); }
