styles:
/**
 * Applies styles for users in high contrast mode. Note that this only applies
 * to Microsoft browsers. Chrome can be included by checking for the `html[hc]`
 * attribute, however Chrome handles high contrast differently.
 */
/* Theme for the ripple elements.*/
/* stylelint-disable material/no-prefixes */
/* stylelint-enable */
:host > div {
  display: grid;
  grid-gap: 0;
  grid-template-columns: 65px auto 1fr auto auto;
  grid-template-rows: 65px;
  grid-template-areas: "logo channels title helpers user"; }
  :host > div div {
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 0 20px; }
    :host > div div:last-child {
      padding-left: 0; }
    :host > div div .dropdown-menu {
      display: flex;
      align-items: center;
      margin: 0;
      cursor: pointer; }
      :host > div div .dropdown-menu .mat-icon {
        margin-left: 3px;
        width: 15px;
        height: 15px;
        font-size: 15px;
        line-height: 15px; }
    :host > div div.logo {
      grid-area: logo;
      border-right: 1px solid #eeeeee; }
    :host > div div.channels {
      grid-area: channels;
      border-right: 1px solid #eeeeee; }
      :host > div div.channels button {
        margin-bottom: 0; }
    :host > div div.app-title {
      grid-area: title; }
      :host > div div.app-title span {
        margin: 0; }
    :host > div div.helper {
      grid-area: helpers; }
      :host > div div.helper ul {
        display: flex;
        align-items: center;
        margin: 0;
        padding: 0;
        list-style: none; }
        :host > div div.helper ul li {
          margin-left: 10px; }
          :host > div div.helper ul li a {
            display: block;
            padding: 5px;
            cursor: pointer;
            color: #757575; }
            :host > div div.helper ul li a:hover {
              color: rgba(0, 0, 0, 0.87); }
    :host > div div.user {
      grid-area: user; }
      :host > div div.user span {
        color: #757575; }
        :host > div div.user span:hover {
          color: rgba(0, 0, 0, 0.87); }
