/**
 * Applies styles for users in high contrast mode. Note that this only applies
 * to Microsoft browsers. Chrome can be included by checking for the `html[hc]`
 * attribute, however Chrome handles high contrast differently.
 */
/* Theme for the ripple elements.*/
/* stylelint-disable material/no-prefixes */
/* stylelint-enable */
:host > ngx-smart-modal section {
  display: flex;
  flex-direction: column;
  flex: 1; }
  :host > ngx-smart-modal section > header {
    display: flex;
    padding: 0 20px;
    height: 65px;
    min-height: 65px;
    background: #fb8c00;
    color: white;
    align-items: center; }
    :host > ngx-smart-modal section > header > .header-title {
      flex: 1;
      margin: 0; }
    :host > ngx-smart-modal section > header > .close-button {
      min-width: 30px;
      max-width: 30px;
      padding: 0;
      margin: 0; }
  :host > ngx-smart-modal section > .modal-content {
    flex: 1;
    width: 100%;
    padding: 20px; }
  :host > ngx-smart-modal section footer {
    display: flex;
    justify-content: flex-end;
    height: 32px;
    min-height: 52px;
    border-top: 1px solid #e0e0e0;
    padding: 8px; }
    :host > ngx-smart-modal section footer .mat-button {
      color: #fb8c00;
      text-transform: uppercase;
      margin-left: 8px; }
      :host > ngx-smart-modal section footer .mat-button:first-of-type {
        margin-left: 0; }
      :host > ngx-smart-modal section footer .mat-button[disabled] {
        color: #9e9e9e; }
